import React, { Component } from "react";
import "./alert-popup.css";

import carCrashIcon from "../assert/car-crash-icon.png";

class AlertPopup extends Component {
  state = {};

  handleLeftButtonClick = () => {
    let recentAlert = this.state.recentAlert;
    let focusAlert = this.state.focusAlert;

    let temp = {};

    recentAlert.map(alert => {
      if (alert.id === focusAlert.id) {
        alert.state = "right";
      }
      if (focusAlert.id > 1) {
        temp = recentAlert[focusAlert.id - 1];
      }
      return alert;
    });

    this.setState({
      recentAlert,
      focusAlert: temp
    });
  };
  handleRightButtonClick = () => {
    let recentAlert = this.state.recentAlert;
    let focusAlert = this.state.focusAlert;

    let temp = {};
    recentAlert.map(alert => {
      if (alert.id === focusAlert.id) {
        alert.state = "left";
      } else if (
        alert.id === focusAlert.id - 1 &&
        alert.id === recentAlert.length
      ) {
        // alert.state = "left";
        console.log("its work man");
      }
      if (focusAlert.id < recentAlert.length) {
        temp = recentAlert[focusAlert.id + 1];
      }

      return alert;
    });

    console.log("temp", temp);

    focusAlert = temp;
    this.setState({
      recentAlert,
      focusAlert
    });
  };

  getPopupboxClasses = alertState => {
    if (alertState === "center") {
      return "popup-box";
    } else if (alertState === "left") {
      return "popup-box-move-left";
    } else if (alertState === "right") {
      return "popup-box-move-right";
    }
  };
  render() {
    return (
      <div className="alert-popup">
        <div className="background-blur" />
        <div className="alert-content-holder">
          <div className="top-content-holder">
            {this.state.recentAlert.map(alert => (
              <div
                className={this.getPopupboxClasses(alert.state)}
                key={alert.id}>
                <div className="img-container">
                  <img src={carCrashIcon} alt="" />
                </div>
                <div className="content">
                  <div className="top-section">
                    <div className="title">{alert.location}</div>
                    <div className="date">{alert.date}</div>
                  </div>
                  <div className="middle-section">
                    <span>{alert.patientName}</span>, {alert.licenseNo}
                  </div>
                  <div className="button-section">
                    <button>Done</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default AlertPopup;
