import React, { Component } from "react";
import "./alert-popup.css";

import carCrashIcon from "../assert/car-crash-icon.png";
import leftIcon from "../assert/chevron-left-icon.png";
import rightIcon from "../assert/chevron-right-icon.png";

class AlertPopup extends Component {
  state = {
    recentAlert: [
      {
        id: 1,
        location: "Location",
        licenseNo: "License No.",
        patientName: "Patient name",
        date: "Date",
        state: "center"
      },
      {
        id: 2,
        location: "Location",
        licenseNo: "License No.",
        patientName: "Patient name",
        date: "Date",
        state: "center"
      },
      {
        id: 3,
        location: "Location",
        licenseNo: "License No.",
        patientName: "Patient name",
        date: "Date",
        state: "center"
      }
    ],
    focusAlert: {
      id: 1,
      location: "Location",
      licenseNo: "License No.",
      patientName: "Patient name",
      date: "Date",
      state: "center"
    }
  };

  handleLeftButtonClick = () => {
    let recentAlert = this.state.recentAlert;
    let focusAlert = this.state.focusAlert;

    recentAlert.map(alert => {
      if (alert.id === focusAlert.id) {
        focusAlert = recentAlert[focusAlert + 1];
        alert.state = "center";
      } else if (
        alert.id === focusAlert.id + 1 &&
        alert.id !== recentAlert.length
      ) {
        alert.state = "left";
      }
      return alert;
    });
  };
  handleRightButtonClick = () => {
    let recentAlert = this.state.recentAlert;
    let focusAlert = this.state.focusAlert;

    recentAlert.map(alert => {
      if (alert.id === focusAlert.id) {
        alert.state = "center";
      } else if (alert.id === focusAlert.id - 1 && alert.id !== 0) {
        alert.state = "right";
      }
      return alert;
    });
  };

  getPopupboxClasses = alertState => {
    if (alertState === "center") {
      return "popup-box";
    } else if (alertState === "left") {
      return "popup-box-move-left";
    } else if (alertState === "right") {
      return "popup-box-move-right";
    }
  };
  render() {
    return (
      <div className="alert-popup">
        <div className="background-blur" />
        <div className="alert-content-holder">
          <div className="top-content-holder">
            <div className="navigation-btn">
              <button>
                <img
                  src={leftIcon}
                  alt="left-btn"
                  onClick={this.handleLeftButtonClick}
                />
              </button>
            </div>
            <div className="popup-stack">
              <div className="popup-stack-holder">
                {this.state.recentAlert.map(alert => (
                  <div
                    className={this.getPopupboxClasses(alert.state)}
                    key={alert.id}>
                    <div className="img-container">
                      <img src={carCrashIcon} alt="" />
                    </div>
                    <div className="content">
                      <div className="top-section">
                        <div className="title">{alert.location}</div>
                        <div className="date">{alert.date}</div>
                      </div>
                      <div className="middle-section">
                        <span>{alert.patientName}</span>, {alert.location}
                      </div>
                      <div className="button-section">
                        <button onClick={this.handleLeftButtonClick}>
                          Done
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="navigation-btn">
              <button>
                <img
                  src={rightIcon}
                  alt="right-btn"
                  onClick={this.handleRightButtonClick}
                />
              </button>
            </div>
          </div>
          <div className="nav-indicator">
            {this.state.recentAlert.map(alert => {
              if (alert.state === "center") {
                return <div className="curr-indicator" key={alert.id} />;
              } else {
                return <div className="indicator" key={alert.id} />;
              }
            })}
          </div>
        </div>
      </div>
    );
  }
}

export default AlertPopup;
