import React, { Component } from "react";

import { connect } from "react-redux";
import { getAccidents, updateAccident } from "../actions/accidentActions";
import { getUsers } from "../actions/userActions";
import { getAmbulances } from "../actions/ambulanceActions";
import PropTypes from "prop-types";

import RecordList from "./record-list";
import AlertPopup from "./alert-popup";
import NotificationCenter from "../components/notification-center/notification-center";

class ContentHolder extends Component {
  state = {
    accident: {
      accidents: []
    },
    user: {},
    ambulance: {},
    clickedAccident: {}
  };

  componentDidMount() {
    this.props.getAccidents();
    this.props.getUsers();
    this.props.getAmbulances();
  }

  getAllServedAccidents = (accidents, users) => {
    if (accidents.length) {
      accidents = accidents.filter(accident => accident.serve);
      console.log(accidents);

      let serverdAccidents = accidents.map(accident => {
        let user = users.filter(
          user => user.vehicle_id === accident.vehicle_id
        );
        user = user[0];
        let formatedAccident = {};
        formatedAccident.id = accident.id;
        formatedAccident.patientName = user.name;
        formatedAccident.descript = [
          {
            key: 0,
            title: "Vehicle no.",
            text: accident.vehicle_no
          },
          {
            key: 1,
            title: "Medical condition",
            text: user.medical_condotion
          },
          {
            key: 2,
            title: "Location",
            text: accident.latitude + ", " + accident.longitude
          }
        ];
        return formatedAccident;
      });

      console.log(serverdAccidents);
      //   allAccidents = allAccidents.sort((a, b) => (a.date < b.date ? 1 : -1));
      //   let servedAccidents = allAccidents.map(accident => {
      //     let formatedAccident = {};
      //     formatedAccident.id = accident.id;
      //     formatedAccident.patientName = accident.name;
      //     formatedAccident.descript = [
      //       {
      //         key: 0,
      //         title: "Vehicle no.",
      //         text: accident.vehicle_no
      //       },
      //       {
      //         key: 1,
      //         title: "Medical condition",
      //         text: accident.medical_condotion
      //       },
      //       {
      //         key: 2,
      //         title: "Location",
      //         text: accident.latitude + ", " + accident.longitude
      //       }
      //     ];
      //     return formatedAccident;
      //   });
      //   return servedAccidents;
    }
    // return {};
  };

  getAllUnServedAccidents = _ => {
    let allAccidents = this.state.accident.accidents;

    allAccidents = allAccidents.filter(accident => accident.served === 0);
    allAccidents = allAccidents.sort((a, b) => (a.date < b.date ? 1 : -1));

    let servedAccidents = allAccidents.map(accident => {
      let formatedAccident = {};

      formatedAccident.id = accident.id;
      formatedAccident.patientName = accident.name;
      formatedAccident.date = accident.date;
      formatedAccident.license_no = accident.license_no;
      formatedAccident.location = accident.location;

      return formatedAccident;
    });

    return servedAccidents;
  };

  render() {
    return (
      <React.Fragment>
        <main>
          <div className="listTitle">All accidents</div>
          <RecordList
            accidents={this.getAllServedAccidents(
              this.props.accident.accidents,
              this.props.user.users
            )}
          />
          {/* <RecordList accidents={this.getAllServedAccidents()} /> */}
        </main>

        <NotificationCenter
          accidents={this.getAllUnServedAccidents()}
          handleNotificationItemClick={this.handleNotificationItemClick}
          handleServedBtnClick={this.handleServedBtnClick}
        />

        {Object.entries(this.state.clickedAccident).length === 0 ? null : (
          <AlertPopup
            accident={this.state.clickedAccident}
            handleAlertBoxClose={this.handleAlertBoxClose}
            handleServedBtnClick={this.handleServedBtnClick}
          />
        )}
      </React.Fragment>
    );
  }
}

ContentHolder.propTypes = {
  getAccidents: PropTypes.func.isRequired,
  accident: PropTypes.object.isRequired,
  user: PropTypes.object.isRequired,
  ambulance: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  accident: state.accident,
  user: state.user,
  ambulance: state.ambulance
});

export default connect(mapStateToProps, {
  getAccidents,
  updateAccident,
  getUsers,
  getAmbulances
})(ContentHolder);
