import React, { Component } from "react";
import "./App.css";
import RecordList from "./components/record-list";
import AlertPopup from "./components/alert-popup";

class App extends Component {
  state = {
    accidents: []
  };

  componentDidMount() {
    this.getAllAccidents();
  }
  getAllAccidents = _ => {
    const scope = this;
    fetch("http://localhost:4000/accidents")
      .then(res => res.json)
      .then(res => scope.setState({ accidents: res.data }))
      .catch(err => console.log(err));
  };

  getAllServedAccidents = () => {
    const allAccidents = this.state.accidents;

    const servedAccidents = allAccidents.map(accident => {
      let formatedAccident = {};

      formatedAccident = accident.id;
      formatedAccident.patientName = accident.name;
      formatedAccident.date = accident.date;

      formatedAccident.descript = [
        {
          key: 1,
          title: "License no.",
          text: accident.license_no
        },
        {
          key: 2,
          title: "Medical condition",
          text: accident.medical_cond
        },
        {
          key: 3,
          title: "Location",
          text: accident.location
        }
      ];
      return formatedAccident;
    });

    console.log(servedAccidents);

    return servedAccidents;
  };

  getAllUnServedAccidents = () => {};
  render() {
    return (
      <div className="App">
        <nav>Post Crash Help</nav>
        <main>
          <div className="listTitle">All accidents</div>
          <RecordList accidents={this.getAllServedAccidents()} />
        </main>
        {/* <AlertPopup /> */}
      </div>
    );
  }
}

export default App;
